INSERT INTO `products` (`id`, `product_category_id`, `name`, `description`, `price`, `featured`, `tag`, `created_at`, `updated_at`) VALUES
(1, 1, 'Áo Polo Nam Trơn Cổ Dệt Gân To Form Fitted - 10S23POL066', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O POLO NAM TRƠN CỔ DỆT G&Acirc;N TO FORM FITTED - 10S23POL066</strong></p>\r\n\r\n<p>Ng&agrave;y nay, &aacute;o polo l&agrave; loại trang phục phổ biến trong tủ đồ của ph&aacute;i nam. N&oacute; lu&ocirc;n l&agrave; m&oacute;n trang phục l&yacute; tưởng cho những ng&agrave;y h&egrave; oi bức nhưng b&ecirc;n cạnh đ&oacute; ch&uacute;ng ta cũng c&oacute; thể sử dụng &aacute;o polo linh hoạt v&agrave;o bất cứ m&ugrave;a n&agrave;o trong năm. Ch&iacute;nh v&igrave; vậy, &aacute;o polo hội tụ đủ những yếu tố m&agrave; mọi người cần: lịch sự, chỉn chu nhưng lại kh&ocirc;ng qu&aacute; formal.</p>\r\n\r\n<p><strong>&Aacute;o Polo Nam Trơn Cổ Dệt G&acirc;n To Form Fitted - 10S23POL066</strong>&nbsp;được thiết kế với phong c&aacute;ch sang trọng v&agrave; thanh lịch. &Aacute;o được l&agrave;m từ chất liệu cotton kết hợp với chi tiết phần nh&atilde;n ở lai &aacute;o gi&uacute;p chiếc &aacute;o c&oacute; điểm nhấn hơn. Phần cổ &aacute;o được dệt g&acirc;n to, l&agrave;m đứng form cổ tr&ocirc;ng bạn nam to&aacute;t l&ecirc;n vẻ đẹp sang trọng, lịch sự. Thiết kế n&agrave;y vừa đem lại vẻ chỉn chu, tao nh&atilde; cho bạn nam vừa đem đến sự tho&aacute;ng m&aacute;t, dễ chịu cho người mặc.</p>\r\n\r\n<p><img alt=\"chat-lieu-10S23POL066\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" /></p>\r\n\r\n<p><img alt=\"hdbq-10S23POL066\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun_1.png\" /></p>', 20, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 12:07:12', '2023-04-08 12:07:12'),
(2, 1, 'Áo Polo Nam Tay Ngắn Phối Màu Cổ Form Fitted - 10F22POL023R1', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O POLO NAM TAY NGẮN PHỐI M&Agrave;U CỔ FORM FITTED - 10F22POL023R1</strong></p>\r\n\r\n<p>Ng&agrave;y nay, &aacute;o polo l&agrave; loại trang phục phổ biến trong tủ đồ của ph&aacute;i nam. N&oacute; lu&ocirc;n l&agrave; m&oacute;n trang phục l&yacute; tưởng cho những ng&agrave;y h&egrave; oi bức nhưng b&ecirc;n cạnh đ&oacute; ch&uacute;ng ta cũng c&oacute; thể sử dụng &aacute;o polo linh hoạt v&agrave;o bất cứ m&ugrave;a n&agrave;o trong năm. Ch&iacute;nh v&igrave; vậy, &aacute;o polo hội tụ đủ những yếu tố m&agrave; mọi người cần: lịch sự, chỉn chu nhưng lại kh&ocirc;ng qu&aacute; formal.</p>\r\n\r\n<p><strong>&Aacute;o Polo Nam Tay Ngắn Phối Cổ Form Fitted - 10F22POL023R1</strong>&nbsp;l&agrave; item thể hiện được sự thanh lịch của c&aacute;c ch&agrave;ng trong mọi t&igrave;nh huống nhưng cũng kh&ocirc;ng k&eacute;m phần năng động. Được may từ chất liệu 100% cotton tho&aacute;ng m&aacute;t, thấm h&uacute;t mồ h&ocirc;i m&agrave; c&ograve;n c&oacute; thểm khả năng co gi&atilde;n cực tốt, độ bền cao. Form &aacute;o fitted vừa vặn với cơ thể gi&uacute;p c&aacute;c ch&agrave;ng t&ocirc;n l&ecirc;n v&oacute;c d&aacute;ng khỏe khoắn. Điểm đặc biệt nhất gi&uacute;p chiếc &aacute;o trở n&ecirc;n nổi bật ch&iacute;nh l&agrave; thiết kế phối m&agrave;u ở phần cổ &aacute;o v&agrave; tay &aacute;o, mang đến cảm gi&aacute;c mới lạ, trẻ trung thể hiện phong c&aacute;ch c&aacute; t&iacute;nh, năng động.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22POL023R1\" src=\"https://routine.vn/media/wysiwyg/Content_product_2/TQ_-_605_-_10F22POL023R1_1.jpg\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22POL023R1\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22POL023R1\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun_1.png\" /></p>', 25, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 12:17:06', '2023-04-08 12:17:06'),
(3, 1, 'Áo Polo Nam Dây Kéo Bo Cổ Phối Màu Form Fitted - 10F22POL024', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O POLO NAM D&Acirc;Y K&Eacute;O BO CỔ PHỐI M&Agrave;U FORM FITTED - 10F22POL024</strong></p>\r\n\r\n<p>Ng&agrave;y nay, &aacute;o polo nam l&agrave; loại trang phục kh&ocirc;ng thể kh&ocirc;ng c&oacute; trong tủ đồ của c&aacute;c ch&agrave;ng trai. N&oacute; lu&ocirc;n l&agrave; m&oacute;n trang phục l&yacute; tưởng cho những ng&agrave;y h&egrave; oi bức nhưng b&ecirc;n cạnh đ&oacute; ch&uacute;ng ta cũng c&oacute; thể sử dụng &aacute;o polo linh hoạt v&agrave;o bất cứ m&ugrave;a n&agrave;o trong năm. Ch&iacute;nh v&igrave; vậy, &aacute;o polo nam hội tụ đủ những yếu tố m&agrave; c&aacute;c ch&agrave;ng trai cần: lịch sự, chỉn chu nhưng lại kh&ocirc;ng qu&aacute; formal.</p>\r\n\r\n<p><strong>&Aacute;o Polo D&acirc;y K&eacute;o Phối Bo Cổ. Fitted - 10F22POL024</strong>&nbsp;c&oacute; hoạ tiết l&agrave; những đường kẻ sọc dọc ở cổ kết hợp với phần d&acirc;y k&eacute;o phối bo cổ &aacute;o l&agrave;m điểm nhấn gi&uacute;p mẫu thiết kế thu h&uacute;t &aacute;nh nh&igrave;n của mọi người hơn. &Aacute;o được thiết kế form fittted với th&acirc;n &aacute;o su&ocirc;ng hỗ trợ c&aacute;c bạn nam thuận tiện khi hoạt động. Hơn nữa, chất liệu cotton của sản phẩm lu&ocirc;n được biết đến rộng r&atilde;i với những đặc trưng như: tho&aacute;ng m&aacute;t, thấm h&uacute;t mồ h&ocirc;i tốt v&agrave; đem lại cảm gi&aacute;c mềm mại khi mặc.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22POL024\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_495_-_10F22POL024_1.jpg\" /></p>\r\n\r\n<p><img alt=\"diem-dac-biet-10F22POL024\" src=\"https://routine.vn/media/wysiwyg/content_2/DDB_-_495_-_10F22POL024.jpg\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22POL024\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22POL024\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun_1.png\" style=\"height:900px; width:900px\" /></p>', 25, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 12:21:54', '2023-04-08 12:21:54'),
(4, 1, 'Áo Sơ Mi Nam Tay Dài Trơn Nẹp Ẩn Form Fitted - 10F21SHL003C', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O SƠ MI NAM TAY D&Agrave;I TRƠN NẸP ẨN FORM FITTED - 10F21SHL003C</strong></p>\r\n\r\n<p>H&igrave;nh ảnh chiếc &aacute;o sơ mi đ&atilde; trở n&ecirc;n quen thuộc c&oacute; thể bắt gặp ở mọi nơi, từ c&ocirc;ng sở đến cuộc sống h&agrave;ng ng&agrave;y. &Aacute;o sơ mi c&oacute; t&ecirc;n gọi bắt nguồn từ tiếng Ph&aacute;p l&agrave; Chemise. Trải d&agrave;i lịch sử h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển, đến hiện tại &aacute;o sơ mi được sử dụng như l&agrave; một loại trang phục phổ biến to&agrave;n cầu.<br />\r\n<br />\r\n<strong>&Aacute;o Sơ Mi Tay D&agrave;i Nam Cotton Form Fitted - 10F21SHL003C</strong>&nbsp;m&agrave;u trắng trơn với phom d&aacute;ng cổ bẻ lịch thiệp, kết hợp c&ugrave;ng nẹp n&uacute;t gọn g&agrave;ng v&agrave; ch&acirc;́t vải cotton mềm mại, tho&aacute;ng m&aacute;t. L&agrave; item basic, dễ phối đồ tạo n&ecirc;n outfit lịch l&atilde;m, tự tin cho ph&aacute;i mạnh.</p>\r\n\r\n<p><img alt=\"tong-quan-10F21SHL003C\" src=\"https://routine.vn/media/wysiwyg/content/Banner_function_cho_E-com-04.png\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F21SHL003C\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>', 35, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 12:27:39', '2023-04-09 07:53:41'),
(5, 1, 'Áo Sơ Mi Nam Tay Dài Oxford Trơn Form Fitted - 10F22SHL002C', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O SƠ MI NAM TAY D&Agrave;I OXFORD TRƠN FORM FITTED - 10F22SHL002C</strong></p>\r\n\r\n<p>Ng&agrave;y nay, ch&uacute;ng ta kh&ocirc;ng chỉ bắt gặp những chiếc &aacute;o sơ mi tay d&agrave;i trong m&ocirc;i trường c&ocirc;ng sở như trước đ&acirc;y m&agrave; c&ograve;n c&oacute; thể thường xuy&ecirc;n nh&igrave;n thấy những bộ outfit c&oacute; sự kết hợp với &aacute;o sơ mi trong đời sống thường ng&agrave;y, c&aacute;c buổi tiệc sang trọng,&hellip; Sở dĩ, sơ mi tay d&agrave;i phổ biến trong thời trang bởi v&igrave; khả năng dễ phối đồ, t&iacute;nh linh hoạt ph&ugrave; hợp trong nhiều ho&agrave;n cảnh v&agrave; cuối c&ugrave;ng ch&iacute;nh l&agrave; l&agrave;m nổi bật được vẻ thanh lịch, chỉn chu v&agrave; sang trọng cho người mặc.</p>\r\n\r\n<p><strong>&Aacute;o Sơ Mi Nam tay D&agrave;i Oxford Trơn Form Fitted - 10F22SHL002C</strong>&nbsp;l&agrave; item v&ocirc; c&ugrave;ng quen thuộc, gần gũi với chất liệu tự nhi&ecirc;n tho&aacute;ng m&aacute;t, thấm h&uacute;t mồ h&ocirc;i tốt. Với thiết kế đặc trưng, &aacute;o vẫn giữ nguy&ecirc;n được n&eacute;t trang nh&atilde; v&agrave; tối giản nhưng kh&ocirc;ng mang lại cảm gi&aacute;c tẻ nhạt. Form d&aacute;ng &ocirc;m vừa vặn, phần th&acirc;n v&agrave; tay &aacute;o su&ocirc;ng, kh&ocirc;ng &ocirc;m sẽ mang đến cảm gi&aacute;c rộng r&atilde;i, dễ chịu cho người mặc v&agrave;o những ng&agrave;y h&egrave; oi bức.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22SHL002C\" src=\"https://routine.vn/media/wysiwyg/Content_product_2/TQ_-_646_-_10F22SHL002C_1.jpg\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22SHL002C\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22SHL002C\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-so-mi_1.png\" /></p>', 10, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 12:32:25', '2023-04-08 12:32:25'),
(6, 1, 'Áo Thun Nam Tay Ngắn Cổ Trụ Trơn Form Fitted - 10S20TSH043CR1', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O THUN NAM TAY NGẮN CỔ TRỤ TRƠN FORM FITTED - 10S20TSH043CR1</strong></p>\r\n\r\n<p>&Aacute;o thun tay ngắn l&agrave; một trong những item cơ bản kh&ocirc;ng c&ograve;n xa lạ thời trang thường ng&agrave;y. Những chiếc &aacute;o thun t-shirt đầu ti&ecirc;n được ra đời v&agrave;o những thập kỷ đầu của thế kỷ XX được d&ugrave;ng để l&agrave;m &aacute;o l&oacute;t của những người l&iacute;nh trong qu&acirc;n đội. Cột mốc quan trọng trở th&agrave;nh item &ldquo;quốc d&acirc;n&rdquo; đ&oacute; l&agrave; khi c&aacute;c diễn vi&ecirc;n Hollywood đưa &aacute;o thun trắng l&ecirc;n m&agrave;n ảnh. Từ đ&oacute;, &aacute;o thun trở th&agrave;nh m&oacute;n đồ thời trang sử dụng phổ biến đến ng&agrave;y nay.</p>\r\n\r\n<p><strong>&Aacute;o Thun Cotton. Fitted Form - 10S20TSH043CR1</strong>&nbsp;c&oacute; thiết kế tay ngắn, form &ocirc;m vừa vặn v&agrave; chất liệu tho&aacute;ng m&aacute;t, mềm mịn. Phần cổ &aacute;o tr&ograve;n kết hợp h&agrave;ng khuya &aacute;o trươc ngực,ống tay ngắn vừa phải với độ co gi&atilde;n tốt gi&uacute;p cho bạn nam mặc l&ecirc;n tr&ocirc;ng rất thoải m&aacute;i v&agrave; tiện lợi. Ngo&agrave;i ra &aacute;o cũng được t&iacute;ch hợp bởi nhiều t&iacute;nh năng vượt trội như tho&aacute;ng m&aacute;t, thấm h&uacute;t được mồ h&ocirc;i, chống m&ugrave;i v&agrave; lưu giữ m&ugrave;i hương tốt hơn.</p>\r\n\r\n<p><img alt=\"chat-lieu-10S20TSH043CR1\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10S20TSH043CR1\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 7, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 12:57:50', '2023-04-09 07:55:24'),
(7, 2, 'Đầm Midi Nữ Tay Ngắn Phối Túi Form Straight - 10F22DREW004', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;ĐẦM MIDI NỮ TAY NGẮN PHỐI T&Uacute;I FORM STRAIGHT - 10F22DREW004</strong></p>\r\n\r\n<p>V&aacute;y đầm l&agrave; loại trang phục kh&ocirc;ng thể thiếu trong tủ đồ của c&aacute;c n&agrave;ng. Ch&uacute;ng c&oacute; thể được xem l&agrave; một trong những loại trang phục c&oacute; tuổi đời l&acirc;u nhất khi xuất hiện từ thế kỷ thứ nhất. Đi theo qua biết bao thời k&igrave; thay đổi, ph&aacute;t triển của thế giới, kiểu d&aacute;ng v&agrave; m&agrave;u sắc của trang phục n&agrave;y cũng trở n&ecirc;n đa dạng, phong ph&uacute; hơn. V&aacute;y đầm dần trở th&agrave;nh mẫu thiết kế được ph&aacute;i nữ ưa chuộng kh&ocirc;ng chỉ v&igrave; sự phong ph&uacute;, đa dạng m&agrave; ch&uacute;ng c&ograve;n gi&uacute;p t&ocirc;n l&ecirc;n được vẻ đẹp nữ t&iacute;nh, thanh lịch v&agrave; gợi cảm cho người phụ nữ.</p>\r\n\r\n<p><strong>Đầm Midi Nữ Tay Ngắn Phối T&uacute;i Form Straight - 10F22DREW004</strong>&nbsp;sở hữu thiết kế su&ocirc;ng rộng thẳng đứng v&ocirc; c&ugrave;ng thoải m&aacute;i khi mặc. Với chất vải tự nhi&ecirc;n n&ecirc;n đảm bảo độ tho&aacute;ng m&aacute;t, thấm h&uacute;t mồ h&ocirc;i tốt v&agrave; dịu nhẹ cho l&agrave;n da. Thiết kế cổ tr&ograve;n đơn giản kết hợp tay ngắn v&ocirc; c&ugrave;ng dễ mặc, điểm đặc biệt chiếc đầm ch&iacute;nh l&agrave; chi tiết ch&acirc;n v&aacute;y đu&ocirc;i c&aacute; midi nhẹ v&agrave; phối t&uacute;i độc đ&aacute;o tạo n&ecirc;n sự nữ t&iacute;nh, bồng bềnh cho chiếc đầm.</p>\r\n\r\n<p><img alt=\"chat-lieu-10F22DREW004\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22DREW004\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-so-mi_1.png\" /></p>', 18, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 13:05:03', '2023-04-09 07:55:56'),
(8, 2, 'Đầm Midi Nữ Xẻ Tà Form Fitted - 10F21DREW004', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;ĐẦM MIDI NỮ XẺ T&Agrave; FORM FITTED - 10F21DREW004</strong></p>\r\n\r\n<p>V&aacute;y đầm l&agrave; loại trang phục kh&ocirc;ng thể thiếu trong tủ đồ của c&aacute;c n&agrave;ng. Ch&uacute;ng c&oacute; thể được xem l&agrave; một trong những loại trang phục c&oacute; tuổi đời l&acirc;u nhất khi xuất hiện từ thế kỷ thứ nhất. Đi theo qua biết bao thời k&igrave; thay đổi, ph&aacute;t triển của thế giới, kiểu d&aacute;ng v&agrave; m&agrave;u sắc của trang phục n&agrave;y cũng trở n&ecirc;n đa dạng, phong ph&uacute; hơn. V&aacute;y đầm dần trở th&agrave;nh mẫu thiết kế được ph&aacute;i nữ ưa chuộng kh&ocirc;ng chỉ v&igrave; sự phong ph&uacute;, đa dạng m&agrave; ch&uacute;ng c&ograve;n gi&uacute;p t&ocirc;n l&ecirc;n được vẻ đẹp nữ t&iacute;nh, thanh lịch v&agrave; gợi cảm cho người phụ nữ.</p>\r\n\r\n<p><strong>Đầm Midi Xẻ T&agrave;. Fit &ndash; 10F21DREW004</strong>&nbsp;c&oacute; form đầm fitted sẽ &ocirc;m theo đường cong cơ thể n&agrave;ng nhưng kh&ocirc;ng bị b&oacute; s&aacute;t khiến người mặc cảm thấy kh&ocirc;ng thoải m&aacute;i. Đường xẻ ở phần tay v&agrave; t&agrave; &aacute;o sẽ khiến cho mẫu thiết kế trở n&ecirc;n trẻ trung hơn nhưng kh&ocirc;ng bị mất đi n&eacute;t thanh lịch vốn c&oacute;. Chất liệu cotton với khả năng thấm h&uacute;t mồ h&ocirc;i ưu việt cũng như cảm gi&aacute;c mềm mại khi mặc đảm bảo đem đến cho n&agrave;ng cảm gi&aacute;c thoải m&aacute;i, dễ chịu nhất khi l&agrave;m việc.</p>\r\n\r\n<p><img alt=\"tong-quan-10F21DREW004\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_299_-_10F21DREW004.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"diem-dac-biet-10F21DREW004\" src=\"https://routine.vn/media/wysiwyg/Content_product_2/_B-299-10F21DREW004.png\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F21DREW004\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F21DREW004\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-so-mi.png\" style=\"height:900px; width:900px\" /></p>', 23, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 13:10:43', '2023-04-09 07:56:24'),
(9, 2, 'Đầm Nữ Xếp Ly Tay Ngắn Khoét Thân Sau Form Fitted - 10S21DREW023', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;ĐẦM NỮ XẾP LY TAY NGẮN KHO&Eacute;T TH&Acirc;N SAU FORM FITTED - 10S21DREW023</strong></p>\r\n\r\n<p>V&aacute;y đầm l&agrave; loại trang phục kh&ocirc;ng thể thiếu trong tủ đồ của c&aacute;c n&agrave;ng. Ch&uacute;ng c&oacute; thể được xem l&agrave; một trong những loại trang phục c&oacute; tuổi đời l&acirc;u nhất khi xuất hiện từ thế kỷ thứ nhất. Đi theo qua biết bao thời k&igrave; thay đổi, ph&aacute;t triển của thế giới, kiểu d&aacute;ng v&agrave; m&agrave;u sắc của trang phục n&agrave;y cũng trở n&ecirc;n đa dạng, phong ph&uacute; hơn. V&aacute;y đầm dần trở th&agrave;nh mẫu thiết kế được ph&aacute;i nữ ưa chuộng kh&ocirc;ng chỉ v&igrave; sự phong ph&uacute;, đa dạng m&agrave; ch&uacute;ng c&ograve;n gi&uacute;p t&ocirc;n l&ecirc;n được vẻ đẹp nữ t&iacute;nh, thanh lịch v&agrave; gợi cảm cho người phụ nữ.</p>\r\n\r\n<p><strong>Đầm Xếp Ly Tay Ngắn Cotton &ndash; 10S21DREW023</strong>&nbsp;sẽ mang đến cảm gi&aacute;c trẻ trung, trong trẻo v&agrave; ngọt ng&agrave;o cho bạn nữ khi diện nhớ v&agrave;o m&agrave;u sắc xanh nhạt của thiết kế. Đầm c&oacute; chất liệu vải xếp ly sẽ gi&uacute;p người mặc tr&ocirc;ng thon gọn, thướt tha v&agrave; thanh lịch hơn. Phần bo cổ v&agrave; phần thắt lưng l&agrave; điểm nhấn của trang phục nhằm gi&uacute;p tổng thể outfit trở n&ecirc;n h&agrave;i ho&agrave; v&agrave; c&acirc;n bằng hơn giữa 2 t&iacute;nh chất năng động, trẻ trung v&agrave; nữ t&iacute;nh, dịu d&agrave;ng.</p>\r\n\r\n<p><img alt=\"tong-quan-10S21DREW023\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_308_-_10S21DREW023.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"diem-dac-biet-10S21DREW023\" src=\"https://routine.vn/media/wysiwyg/content_2/_B-308-10S21DREW023.jpg\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10S21DREW023\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10S21DREW023\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-so-mi.png\" style=\"height:900px; width:900px\" /></p>', 23, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 13:16:42', '2023-04-08 13:16:42'),
(10, 2, 'Áo Vest Nữ Tay Lửng Xẻ Sau Cài Nút Form Relax - 10F21VESW001', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O VEST BLAZER NỮ TAY LỬNG T&Uacute;I ĐẮP TRƠN FORM RELAX - 10F22VESW001</strong></p>\r\n\r\n<p>Khi nhắc đến trang phục vest, người ta thường sẽ nghĩ ngay đến c&aacute;c qu&yacute; &ocirc;ng. Tuy nhi&ecirc;n, trong thời đại ng&agrave;y nay &aacute;o vest cũng được ph&aacute;i đẹp ưa chuộng v&agrave; sử dụng nhiều. Những bộ vest nữ th&ocirc;ng thường sẽ được cắt may ph&ugrave; vừa vặn với v&oacute;c d&aacute;ng của n&agrave;ng cũng như được thiết kế c&aacute;ch điệu hơn nhằm mang đến sự h&agrave;i ho&agrave;, thanh lịch, sang trọng, chuy&ecirc;n nghiệp cho người mặc, đồng thời tăng khả năng ứng dụng của trang phục.</p>\r\n\r\n<p><strong>&Aacute;o Blazer Tweed Tay Ngắn T&uacute;i Đắp. Relax &ndash; 10F22VESW001</strong>&nbsp;khi mặc sẽ t&ocirc;n l&ecirc;n được v&oacute;c d&aacute;ng mảnh mai, c&acirc;n đối của bạn nữ nhờ độ &ocirc;m nhẹ ở phần th&acirc;n v&agrave; tay &aacute;o. Đối với bạn n&agrave;o hay qu&ecirc;n th&igrave; sản phẩm n&agrave;y chắc chắn sẽ lấy l&ograve;ng được c&aacute;c n&agrave;ng nhờ chi tiết t&uacute;i đối xứng th&acirc;n &aacute;o vừa mang lại n&eacute;t hiện đại lại vừa thuận tiện cho người mặc. &Aacute;o c&oacute; thiết kế 2 khuy với đặc trưng l&agrave; mang đến phong th&aacute;i năng động, trẻ trung.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22VESW001\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_484_-_10F22VESW001.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22VESW001\" src=\"https://routine.vn/media/wysiwyg/content/Polyester.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22VESW001\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-so-mi.png\" style=\"height:900px; width:900px\" /></p>', 28, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 15:43:34', '2023-04-08 15:45:19'),
(11, 2, 'Áo Thun Nữ Tay Ngắn In Thêu Form Regular - 10F21TSSW010', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O THUN NỮ TAY NGẮN IN TH&Ecirc;U FORM REGULAR - 10F21TSSW010</strong></p>\r\n\r\n<p>&Aacute;o thun tay ngắn l&agrave; một trong những item cơ bản phổ biến trong thời trang thường ng&agrave;y. Với tổng thể đơn giản, thoải m&aacute;i v&agrave; t&iacute;nh ứng dụng cao, &aacute;o thun lu&ocirc;n l&agrave; lựa chọn ưu ti&ecirc;n nhiều người.</p>\r\n\r\n<p><strong>&Aacute;o Thun Tay Ngắn Nữ Cotton Form Regular - 10F21TSSW010</strong>&nbsp;mang theo phong c&aacute;ch trẻ trung, thoải m&aacute;i với h&igrave;nh in th&ecirc;u l&agrave;m nổi bật ở giữa &aacute;o. Thiết kế tay ngắn, form &aacute;o &ocirc;m v&agrave;o cơ thể vừa vặn, phần &aacute;o su&ocirc;n thẳng tạo cảm gi&aacute;c năng động, tươi mới.</p>\r\n\r\n<p><img alt=\"tong-quan-10F21TSSW010\" src=\"https://routine.vn/media/wysiwyg/content_2/TQ_-_66_-10F21TSSW010.jpg\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F21TSSW010\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F21TSSW010\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 13, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 15:47:00', '2023-04-08 15:47:00'),
(12, 2, 'Áo Khoác Vest Nữ Crop Lai Tay Dài Trơn Form Relax - 10S21VESW002', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O KHO&Aacute;C VEST NỮ CROP LAI TAY D&Agrave;I TRƠN FORM RELAX - 10S21VESW002</strong></p>\r\n\r\n<p>Khi nhắc đến trang phục vest, người ta thường sẽ nghĩ ngay đến c&aacute;c qu&yacute; &ocirc;ng. Tuy nhi&ecirc;n, trong thời đại ng&agrave;y nay &aacute;o vest cũng được ph&aacute;i đẹp ưa chuộng v&agrave; sử dụng nhiều. Những bộ vest nữ th&ocirc;ng thường sẽ được cắt may ph&ugrave; vừa vặn với v&oacute;c d&aacute;ng của n&agrave;ng cũng như được thiết kế c&aacute;ch điệu hơn nhằm mang đến sự h&agrave;i ho&agrave;, thanh lịch, sang trọng, chuy&ecirc;n nghiệp cho người mặc, đồng thời tăng khả năng ứng dụng của trang phục.</p>\r\n\r\n<p><strong>&Aacute;o Vest Nữ &ndash; 10S21VESW002</strong>&nbsp;l&agrave; sản phẩm ch&acirc;n &aacute;i d&agrave;nh cho c&aacute;c c&ocirc; n&agrave;ng c&aacute; t&iacute;nh, th&iacute;ch phong c&aacute;ch năng động, trẻ trung nhưng vẫn giữ được n&eacute;t lịch sự cần c&oacute; trong m&ocirc;i trường c&ocirc;ng sở. Với thiết kế d&aacute;ng &aacute;o lửng v&agrave; hơi &ocirc;m nhẹ ở phần th&acirc;n v&agrave; tay &aacute;o kết hợp với đặc điểm của loại vest 1 khuy sẽ tạo n&ecirc;n bộ outfit trẻ trung, năng động, thời trang cho c&aacute;c n&agrave;ng. Đồng thời, với chất liệu linen thấm h&uacute;t mồ h&ocirc;i tốt sẽ hỗ trợ c&aacute;c n&agrave;ng thoải m&aacute;i hoạt động cả ng&agrave;y.</p>\r\n\r\n<p><img alt=\"tong-quan-10S21VESW002\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_270_-_10S21VESW002.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"diem-dac-biet-10S21VESW002\" src=\"https://routine.vn/media/wysiwyg/content/_B-270-10S21VESW002-min.png\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10S21VESW002\" src=\"https://routine.vn/media/wysiwyg/content/Linen-min.png\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10S21VESW002\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-so-mi.png\" style=\"height:900px; width:900px\" /></p>', 23, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 15:52:40', '2023-04-08 15:52:40'),
(16, 1, 'Áo Khoác Len Cardigan Nam Dệt Kiểu Form Regular - 10F22KNI008', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O KHO&Aacute;C LEN CARDIGAN NAM DỆT KIỂU FORM REGULAR - 10F22KNI008</strong></p>\r\n\r\n<p>Trong tiết trời n&oacute;ng lạnh thất thường mỗi khi giao m&ugrave;a, ch&uacute;ng ta thường sẽ kh&ocirc;ng biết phải lựa chọn bộ trang phục với chất vải thế n&agrave;o để ho&agrave;n to&agrave;n ph&ugrave; hợp với nhiệt độ. V&agrave; với thời điểm đặc biệt như n&agrave;y, &aacute;o len dệt kim sẽ l&agrave; lựa chọn ho&agrave;n hảo nhất để mọi người c&oacute; thể tự tin diện khi dạo phố, đi l&agrave;m, hẹn h&ograve;,&hellip; bất chấp thời tiết thay đổi thế n&agrave;o. &Aacute;o len dệt kim cũng c&oacute; đa dạng mẫu m&atilde; kh&aacute;c nhau như cardigan dệt kim, v&aacute;y dệt kim, &aacute;o len dệt kim cổ cao,&hellip; V&agrave; nhiệm vụ của ch&uacute;ng ta đ&oacute; l&agrave; chọn được kiểu &aacute;o ưng &yacute; v&agrave; mix-match để bộ outfit của m&igrave;nh trở n&ecirc;n thật ấn tượng.</p>\r\n\r\n<p><strong>&Aacute;o cardigan dệt kim dệt kiểu. Regular - 10F22KNI008</strong>&nbsp;l&agrave; loại &aacute;o kho&aacute;c len được th&ecirc;u dệt n&uacute;t c&agrave;i ở ph&iacute;a trước. Đ&acirc;y l&agrave; kiểu &aacute;o ho&agrave;n hảo cho m&ugrave;a Thu &ndash; Đ&ocirc;ng bởi cardigan được thiết kế nhằm giữ ấm cơ thể v&agrave; c&oacute; thể kết hợp với nhiều loại trang phục, đồng thời, mang lại n&eacute;t đẹp tao nh&atilde;, thanh lịch cho bạn nam. &Aacute;o được l&agrave;m chủ yếu từ 3 chất liệu visco, nylon v&agrave; polyester gi&uacute;p cho vải mềm, ấm v&agrave; đặc biệt &iacute;t bị nhăn. B&ecirc;n cạnh đ&oacute; &aacute;o cũng được thiết kế với kiểu d&aacute;ng cổ điển, kh&ocirc;ng &ocirc;m b&oacute; s&aacute;t v&agrave;o phần cơ thể tạo cho người mặc sự dễ chịu, thoải m&aacute;i khi hoạt động.</p>\r\n\r\n<p><img alt=\"chat-lieu-10F22KNI008\" src=\"https://routine.vn/media/wysiwyg/content/CL_14_Acrylic-min.png\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22KNI008\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 25, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 16:18:35', '2023-04-09 07:56:52'),
(17, 1, 'Áo Len Dệt Kim Nam Tay Dài Sọc Ngang Form Regular - 10F22KNI012', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O LEN DỆT KIM NAM TAY D&Agrave;I SỌC NGANG FORM REGULAR - 10F22KNI012</strong></p>\r\n\r\n<p>Trong tiết trời n&oacute;ng lạnh thất thường mỗi khi giao m&ugrave;a, ch&uacute;ng ta thường sẽ kh&ocirc;ng biết phải lựa chọn bộ trang phục với chất vải thế n&agrave;o để ho&agrave;n to&agrave;n ph&ugrave; hợp với nhiệt độ. V&agrave; với thời điểm đặc biệt như n&agrave;y, &aacute;o len dệt kim sẽ l&agrave; lựa chọn ho&agrave;n hảo nhất để mọi người c&oacute; thể tự tin diện khi dạo phố, đi l&agrave;m, hẹn h&ograve;,&hellip; bất chấp thời tiết thay đổi thế n&agrave;o. &Aacute;o len dệt kim cũng c&oacute; đa dạng mẫu m&atilde; kh&aacute;c nhau như cardigan dệt kim, v&aacute;y dệt kim, &aacute;o len dệt kim cổ cao,&hellip; V&agrave; nhiệm vụ của ch&uacute;ng ta đ&oacute; l&agrave; chọn được kiểu &aacute;o ưng &yacute; v&agrave; mix-match l&agrave;m sao để bộ outfit của m&igrave;nh trở n&ecirc;n thật ấn tượng.</p>\r\n\r\n<p><strong>&Aacute;o len dệt kim nam Tay d&agrave;i Sọc ngang form Regular - 10F22KNI012</strong>&nbsp;được l&agrave;m từ chất vải tổng hợp thay thế cho c&aacute;c sợi len, loại vải được sử rộng rất rộng r&atilde;i trong ng&agrave;nh c&ocirc;ng nghiệp dệt kim. Với form &aacute;o vừa vặn, hơi &ocirc;m nhẹ v&agrave;o người v&agrave; c&oacute; phần lay &aacute;o v&agrave; cổ tay &aacute;o b&oacute; v&agrave;o. Kết hợp c&ugrave;ng họa tiết kẻ sọc trẻ trung, tất cả c&ugrave;ng tạo n&ecirc;n một chiếc &aacute;o năng động, c&aacute; t&iacute;nh v&agrave; tiện dụng.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22KNI012\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_287_-_10F22KNI012.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"diem-dac-biet-10F22KNI012\" src=\"https://routine.vn/media/wysiwyg/content_2/_i_m_c_bi_t_-_Dot_6__B-287-10F22KNI012.jpg\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22KNI012\" src=\"https://routine.vn/media/wysiwyg/content/CL_14_Acrylic-min.png\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22KNI012\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 30, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 16:21:43', '2023-04-09 07:57:48'),
(18, 1, 'Áo Len Dệt Kim Nam Tay Dài Cổ Tròn Form Regular - 10F22KNI010', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O LEN DỆT KIM NAM TAY D&Agrave;I CỔ TR&Ograve;N FORM REGULAR - 10F22KNI010</strong></p>\r\n\r\n<p>Với sự ph&aacute;t triển của thời trang, &aacute;o len đang ng&agrave;y một được cải tiến v&agrave; đa dạng về thiết kế hơn để trở th&agrave;nh thường phục h&agrave;ng ng&agrave;y. Ngo&agrave;i những kiểu d&aacute;ng truyền thống, c&aacute;c nh&atilde;n h&agrave;ng thời trang đ&atilde; kh&ocirc;ng ngừng tạo ra nhiều kiểu d&aacute;ng độc lạ, mới mẽ hơn để c&aacute;c t&iacute;n đồ &aacute;o len thỏa sức lựa chọn cho m&igrave;nh một item ph&ugrave; hợp nhất.</p>\r\n\r\n<p><strong>&Aacute;o Dệt Kim Tay D&agrave;i Cổ Tr&ograve;n Phối Sọc. Regular - 10F22KNI010</strong>&nbsp;c&oacute; form &aacute;o Regular với đặc trưng l&agrave; phần &aacute;o su&ocirc;ng thẳng kh&ocirc;ng qu&aacute; rộng gi&uacute;p ph&aacute;i mạnh c&oacute; thể che được những điểm m&agrave; bản th&acirc;n chưa tự tin nhưng kh&ocirc;ng mang lại cảm gi&aacute;c l&ugrave;ng b&ugrave;ng. Hơn nữa, với điểm nhấn l&agrave; m&agrave;u sắc được phối một c&aacute;ch tinh tế, h&agrave;i ho&agrave; đ&atilde; gi&uacute;p mẫu &aacute;o với thiết kế cổ tr&ograve;n, tay d&agrave;i basic n&agrave;y trở n&ecirc;n vừa dễ phối đồ vừa kh&ocirc;ng mang cảm gi&aacute;c đơn điệu. Đặc biệt, khả năng giữ ấm cũng như nhanh kh&ocirc; của chất liệu acrylic sẽ l&agrave; lựa chọn tuyệt vời với kh&iacute; hậu lạnh ẩm trong m&ugrave;a thu đ&ocirc;ng.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22KNI010\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_223_-_10F22KNI010.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"diem-dac-biet-10F22KNI010\" src=\"https://routine.vn/media/wysiwyg/content/_B_222_10F22KNI009-min.png\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22KNI010\" src=\"https://routine.vn/media/wysiwyg/content/CL_14_Acrylic-min.png\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22KNI010\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 25, 1, '[\"Qu\\u1ea7n \\u00c1o Nam\"]', '2023-04-08 16:24:34', '2023-04-08 16:24:34'),
(19, 2, 'Áo Nỉ Sweater Nữ Phối Tay In Form Regular - 10F22SWEW005', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O NỈ SWEATER NỮ PHỐI TAY IN FORM REGULAR - 10F22SWEW005</strong></p>\r\n\r\n<p>Vải nỉ đ&atilde; được phổ biến tr&ecirc;n thị trường từ những năm 1990. Qua qu&aacute; tr&igrave;nh ph&aacute;t triển v&agrave; cải tiến, vải nỉ đ&atilde; chiếm lĩnh một phần kh&ocirc;ng nhỏ trong thị trường vải v&oacute;c. V&agrave; trong lĩnh vực thời trang, nỉ thường được sử dụng để thiết kế n&ecirc;n c&aacute;c loại quần &aacute;o như &aacute;o hoodie, &aacute;o sweater, đồ trẻ em, đồ ngủ,...</p>\r\n\r\n<p><strong>&Aacute;o Sweatshirt Phối Tay In. Regular &ndash; 10F22SWEW005</strong>&nbsp;l&agrave; sản phẩm đậm chất thể thao, năng động với d&aacute;ng &aacute;o su&ocirc;ng thẳng nhưng vẫn vừa vặn với cơ thể người mặc. &Aacute;o c&oacute; 2 m&agrave;u sắc l&agrave; trắng v&agrave; hồng nhạt đảm bảo kh&aacute;ch h&agrave;ng c&oacute; thể lựa chọn được sản phẩm đ&uacute;ng theo c&aacute; t&iacute;nh v&agrave; sở th&iacute;ch ri&ecirc;ng của m&igrave;nh. Đ&acirc;y cũng l&agrave; bộ trang phục &ldquo;must have&rdquo; trong m&ugrave;a đ&ocirc;ng v&igrave; khả năng giữ ấm ưu việt của chất liệu nỉ sẽ gi&uacute;p n&agrave;ng lu&ocirc;n cảm thấy ấm &aacute;p trong tiết trời c&oacute; phần gi&aacute; lạnh.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22SWEW005\" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_416_-_10F22SWEW005.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22SWEW005\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22SWEW005\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 20, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 16:31:48', '2023-04-08 16:31:48'),
(20, 2, 'Áo Nỉ Sweater Nữ Tay Dài Trơn Form Regular - 10F22SWEW001', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O NỈ SWEATER NỮ TAY D&Agrave;I TRƠN FORM REGULAR - 10F22SWEW001</strong></p>\r\n\r\n<p>Vải nỉ đ&atilde; được phổ biến tr&ecirc;n thị trường từ những năm 1990. Qua qu&aacute; tr&igrave;nh ph&aacute;t triển v&agrave; cải tiến, vải nỉ đ&atilde; chiếm lĩnh một phần kh&ocirc;ng nhỏ trong thị trường vải v&oacute;c. V&agrave; trong lĩnh vực thời trang, nỉ thường được sử dụng để thiết kế n&ecirc;n c&aacute;c loại quần &aacute;o như &aacute;o hoodie, &aacute;o sweater, đồ trẻ em, đồ ngủ,...</p>\r\n\r\n<p><strong>&Aacute;o Sweatshirt Nữ Regular &ndash; 10F22SWEW001</strong>&nbsp;l&agrave; sản phẩm đậm chất thể thao, năng động với d&aacute;ng &aacute;o su&ocirc;ng thẳng nhưng vẫn vừa vặn với cơ thể người mặc. Ngo&agrave;i những m&agrave;u sắc cơ bản l&agrave; trắng, đen gi&uacute;p kh&aacute;ch h&agrave;ng c&oacute; thể phối đồ v&agrave; phụ kiện dễ d&agrave;ng th&igrave; sản phẩm n&agrave;y c&ograve;n c&oacute; 2 m&agrave;u pastel rất ph&ugrave; hợp với xu hướng thời trang giới trẻ hiện nay.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22SWEW001 \" src=\"https://routine.vn/media/wysiwyg/content/TQ_-_441_-_10F22SWEW001.jpg\" style=\"height:900px; width:900px\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22SWEW001 \" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:600px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbd-10F22SWEW001 \" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 27, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 16:34:39', '2023-04-08 16:34:39'),
(21, 2, 'Áo Hoodie Nữ Tay Dài Có Mũ Nhãn Trang Trí - 10F22HODW005', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;&Aacute;O HOODIE NỮ TAY D&Agrave;I C&Oacute; MŨ NH&Atilde;N TRANG TR&Iacute; - 10F22HODW005</strong></p>\r\n\r\n<p>Ngo&agrave;i &aacute;o thun, th&igrave; &aacute;o hoodie được xem l&agrave; một trong những item cần thiết v&agrave; c&oacute; thể sử dụng được ở nhiều nơi. V&agrave; đ&oacute; cũng l&agrave; một trong những loại trang phục streetstyle, được cả thế giới ưa chuộng, kh&ocirc;ng ri&ecirc;ng g&igrave; đối với Việt Nam đặc biệt l&agrave; giới trẻ. Nhờ v&agrave;o form &aacute;o rộng r&atilde;i v&agrave; thoải m&aacute;i khiến cho người d&ugrave;ng c&oacute; thể phối hợp được với nhiều loại trang phục, ho&agrave;n cảnh kh&aacute;c nhau.</p>\r\n\r\n<p><strong>&Aacute;o Hoodie Nh&atilde;n Trang Tr&iacute; &ndash; 10F22HODW005</strong>&nbsp;l&agrave; sản phẩm m&agrave; những bạn nữ ưa chuộng phong c&aacute;ch thể thao năng động nhưng kh&ocirc;ng k&eacute;m phần đ&aacute;ng y&ecirc;u. Form &aacute;o regular với đặc điểm su&ocirc;ng, vừa vặn với cơ thể sẽ che được một số điểm m&agrave; n&agrave;ng chưa tự tin ở phần th&acirc;n tr&ecirc;n nhưng vẫn mang đến vẻ ngo&agrave;i gọn g&agrave;ng v&agrave; xinh đẹp. Sản phẩm c&oacute; phần d&acirc;y r&uacute;t b&ecirc;n dưới th&acirc;n &aacute;o gi&uacute;p người mặc c&oacute; thể thoải m&aacute;i điều chỉnh độ bo của &aacute;o. 2 tone m&agrave;u pastel đầy thời trang v&agrave; đ&aacute;ng y&ecirc;u đảm bảo c&oacute; thể đốn tim n&agrave;ng ngay lập tức. Đặc biệt, sản phẩm c&ograve;n c&oacute; chi tiết tag đ&iacute;nh tr&ecirc;n n&oacute;n sẽ l&agrave;m nổi bật sự trẻ trung v&agrave; năng động hơn cho outfit.</p>\r\n\r\n<p><img alt=\"tong-quan-10F22HODW005\" src=\"https://routine.vn/media/wysiwyg/content_2/TQ_-_476_-_10F22HODW005.jpg\" /></p>\r\n\r\n<p><img alt=\"chat-lieu-10F22HODW005\" src=\"https://routine.vn/media/wysiwyg/content/Cotton.jpg\" style=\"height:603px; width:900px\" /></p>\r\n\r\n<p><img alt=\"hdbq-10F22HODW005\" src=\"https://routine.vn/media/wysiwyg/content/hdbq-ao-thun.png\" style=\"height:900px; width:900px\" /></p>', 21, 1, '[\"Qu\\u1ea7n \\u00c1o N\\u1eef\"]', '2023-04-08 16:37:41', '2023-04-08 16:37:41'),
(26, 7, 'Vớ Dài Thể Thao Trơn Logo Freesize - 10S22SOC001', '<p>TH&Ocirc;NG TIN CHI TIẾT<strong>&nbsp;VỚ D&Agrave;I THỂ THAO TRƠN LOGO FREESIZE - 10S22SOC001</strong></p>\r\n\r\n<p><strong>Vớ D&agrave;i Trơn Logo. Freesize &ndash; 10S22SOC001</strong>&nbsp;l&agrave; form vớ rất phổ biến bởi khả năng ứng dụng cao của n&oacute;. D&aacute;ng vớ d&agrave;i n&agrave;y c&oacute; thể ph&ugrave; hợp với mọi phong c&aacute;ch outfit từ trẻ trung, năng động khi phối với sneakers đến lịch l&atilde;m, trưởng th&agrave;nh khi diện c&ugrave;ng gi&agrave;y t&acirc;y, loafer. Sản phẩm c&oacute; tổng cộng 18 m&agrave;u được trải d&agrave;i từ những m&agrave;u sắc nổi bật, trẻ trung đến c&aacute;c gam m&agrave;u trung t&iacute;nh nhằm ph&ugrave; hợp với mục đ&iacute;ch sử dụng của kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p><img alt=\"chat-lieu-10S22SOC001\" src=\"https://routine.vn/media/wysiwyg/content/Polyester.jpg\" style=\"height:603px; width:900px\" /></p>', 3, 1, '[\"T\\u1ea5t\"]', '2023-04-09 02:23:54', '2023-04-09 02:23:54'),
